// overload of Mesh::ehd method
Eigen::VectorXd Mesh::ehd(double (*fp)(double x, double y), const Eigen::VectorXd &h, const Eigen::VectorXd &f)
{
	Eigen::VectorXd tmp = h;
	tmp.setZero();
	for (int i = 0; i < h.size(); i++)
	{
		tmp(i) = (*fp)(h(i), f(i)); // functional pointer takes two variables h and f
	}
	return tmp;
}

// overload of Mesh::ehd method
Eigen::VectorXd Mesh::ehd(double (*fp)(double x), const Eigen::VectorXd &h)
{
	Eigen::VectorXd tmp = h;
	tmp.setZero();
	for (int i = 0; i < h.size(); i++)
	{
		tmp(i) = (*fp)(h(i)); // functional pointer only takes one variable h
	}
	return tmp;
}

double Mesh::oneMassOne_ = 0.0;

void Mesh::initNode()
{ //Initialize finite element nodes
	node.reserve(info.nx * info.ny * 5);

	double dx = info.lx / info.nx, dy = info.ly / info.ny;

	for (int iy = 0; iy < info.ny; iy++)
	{
		std::vector<double> pt(2);
		for (int ix = 0; ix < info.nx; ix++)
		{
			pt[0] = dx * ix;
			pt[1] = dy * iy;
			node.push_back(pt);
			pt[0] = dx * ix + dx / 2;
			node.push_back(pt);
		}
		for (int ix = 0; ix < info.nx; ix++)
		{
			pt[0] = dx * ix;
			pt[1] = dy * iy + dy / 2;
			node.push_back(pt);
			pt[0] = dx * ix + dx / 2;
			node.push_back(pt);
		}
	}
	info.dof = node.size();
	info.joc = (info.lx / info.nx / 2) * (info.ly / info.ny / 2);
	info.jx = (info.lx / info.nx / 2);
	info.jy = (info.ly / info.ny / 2);
}

void Mesh::initElement()
{ // initialize elements
	element.resize(info.nx * info.ny);
	for (auto &it : element)
	{
		it.resize(9); //each element has 9 local DoF
	}
	// each element is a collection of nodal indices that belongs to it
	for (int iy = 0; iy < info.ny; iy++)
	{
		for (int ix = 0; ix < info.nx; ix++)
		{
			element[info.nx * iy + ix] = {// add global nodal indices that belong to the (ix,iy)-element
										  4 * info.nx * iy + 2 * ix,
										  4 * info.nx * iy + 2 * ix + 1,
										  4 * info.nx * iy + (2 * ix + 2) % (2 * info.nx),
										  4 * info.nx * iy + 2 * info.nx + 2 * ix,
										  4 * info.nx * iy + 2 * info.nx + 2 * ix + 1,
										  4 * info.nx * iy + 2 * info.nx + (2 * ix + 2) % (2 * info.nx),
										  (4 * info.nx * iy + 4 * info.nx + 2 * ix) % ((int)node.size()),
										  (4 * info.nx * iy + 4 * info.nx + 2 * ix + 1) % ((int)node.size()),
										  (4 * info.nx * iy + 4 * info.nx + (2 * ix + 2) % (2 * info.nx)) % ((int)node.size())};
		}
	}
}

void Mesh::assembleMass()
{
	consistentMassMatrix.resize(node.size(), node.size());
	consistentMassMatrix.reserve(Eigen::VectorXi::Constant((int)node.size(), 25));
	for (auto &it : element)
	{ // elemental-wise assembly procedure discussed in equation (3.100-3.102)
		for (int i = 0; i < 9; i++)
		{
			for (int j = 0; j < 9; j++)
			{
				consistentMassMatrix.coeffRef(it[i], it[j]) += localMassMatrix[i][j];
			}
		}
	}
	// true mass matrix (no lumping)
	consistentMassMatrix = consistentMassMatrix * (info.lx / info.nx / 2) * (info.ly / info.ny / 2);
	// mass lumping
	lumpedMassMatrix.resize(node.size(), node.size());
	lumpedMassMatrix.reserve(Eigen::VectorXi::Constant((int)node.size(), 1));
	inverseMassMatrix.resize(node.size(), node.size());
	inverseMassMatrix.reserve(Eigen::VectorXi::Constant((int)node.size(), 1));
#pragma omp parallel for num_threads(NUM_THREADS)
	for (int i = 0; i < lumpedMassMatrix.cols(); i++)
	{
		double sum = consistentMassMatrix.col(i).sum(); // col sum of true mass matrix
		lumpedMassMatrix.insert(i, i) = sum;
		inverseMassMatrix.insert(i, i) = 1. / sum; // inverse of lumped diagonal mass matrix
	}

	// one vector  which is unity at all nodes
	Eigen::VectorXd one = Eigen::VectorXd::Ones(info.dof);
	// one vector dot lumped mass dot one vector , should be total Mesh area
	oneMassOne_ = one.dot(lumpedMassMatrix * one);
}

void Mesh::assembleStiff()
{
	stiffnessMatrix.resize(node.size(), node.size());
	stiffnessMatrix.reserve(Eigen::VectorXi::Constant((int)node.size(), 25));
	for (auto &it : element)
	{ // elemental-wise assembly procedure for stiffyness matrix
		for (int i = 0; i < 9; i++)
		{
			for (int j = 0; j < 9; j++)
			{
				double sum = 0;
				for (int k = 0; k < 9; k++)
				{ // local stiffness equation (3.112)
					sum += gaussQuadrature09[k][2] * (info.jy / info.jx * stiffDxAtQuadrature[k][i][j] +
													  info.jx / info.jy * stiffDyAtQuadrature[k][i][j]);
				}
				stiffnessMatrix.coeffRef(it[i], it[j]) += sum; // numerical integration with gauss quadrature
			}
		}
	}
	lumpedLaplaceMatrix.resize(node.size(), node.size());
	lumpedLaplaceMatrix.reserve(Eigen::VectorXi::Constant((int)node.size(), 25));
	lumpedLaplaceMatrix = -inverseMassMatrix * stiffnessMatrix; // laplace matrix = -1 * mass^-1 * stiff matrix
};

void Mesh::assembleWeightedStiff(Eigen::SparseMatrix<double> &matrix, const Eigen::VectorXd &h)
{
	matrix.resize(info.dof, info.dof);
	matrix.reserve(Eigen::VectorXi::Constant(info.dof, 25));
	int partition = NUM_THREADS;
	int inner = ceil((double)info.ny / (double)partition);

	// parallel assembling procedure utilizing all CPUs at same time to assemble
	for (int i = 0; i < inner; i++)
	{
#pragma omp parallel for num_threads(partition)
		for (int j = 0; j < partition; j++)
		{
			if (inner * j + i < info.dof)
			{
				for (int ei = 0; ei < info.nx; ei++)
				{
					std::vector<int> &it = element[(inner * j + i) * info.nx + ei];
					double local_h[9] = {h(it[0]), h(it[1]), h(it[2]), h(it[3]), h(it[4]), h(it[5]), h(it[6]), h(it[7]), h(it[8])};
					//========== BEGIN ========== NUMERICAL INTEGRATION WAY ==========//
					/*				double local_hAtQuadrature[9];
					for (int k = 0; k < 9; k++) {
						double sum = 0;
						for (int nBasis = 0; nBasis < 9; nBasis++) {
							sum += basisAtQuadrature[k][nBasis] * local_h[nBasis];
						}
						local_hAtQuadrature[k] = sum;
					}

					for (int i = 0; i < 9; i++) {
						for (int j = 0; j < 9; j++) {
							double sum = 0;
							for (int k = 0; k < 9; k++) {
								sum += Mesh::gaussQuadrature09[k][2] * local_hAtQuadrature[k] *
									(info.jy/info.jx*Mesh::stiffDxAtQuadrature[k][i][j] + info.jx / info.jy*Mesh::stiffDyAtQuadrature[k][i][j]);
							}
							matrix.coeffRef(it[i], it[j]) += sum;
						}
					}*/
					//========== END ========== NUMERICAL INTEGRATION WAY ==========//
					for (int i = 0; i < 9; i++)
					{
						for (int j = 0; j < 9; j++)
						{
							double sum = 0;
							for (int k = 0; k < 9; k++)
							{ // load pre-defined analytic integration
								sum += local_h[k] * (info.jy / info.jx * Mesh::weightedStiffDx[k][i][j] +
													 info.jx / info.jy * Mesh::weightedStiffDy[k][i][j]); // equation (3.114)
							}
							matrix.coeffRef(it[i], it[j]) += sum;
						}
					}
				}
			}
		}
	}
};

void Mesh::assembleWeightedStiff(
	Eigen::SparseMatrix<double> &W, const Eigen::VectorXd &H3,
	Eigen::SparseMatrix<double> &dW, const Eigen::VectorXd &dH3dH, const Eigen::VectorXd &P)
{
	// ---- Allocation sparse matrix
	W.resize(info.dof, info.dof);
	dW.resize(info.dof, info.dof);
	W.reserve(Eigen::VectorXi::Constant(info.dof, 25));
	dW.reserve(Eigen::VectorXi::Constant(info.dof, 25));
	// ---- Allocation sparse matrix
	int partition = NUM_THREADS;
	int inner = ceil((double)info.ny / (double)partition);
	for (int i = 0; i < inner; i++)
	{
#pragma omp parallel for num_threads(partition)
		for (int j = 0; j < partition; j++)
		{
			if (inner * j + i < info.dof)
			{
				for (int ei = 0; ei < info.nx; ei++)
				{ // elemental-wise compute equation (3.107)
					std::vector<int> &it = element[(inner * j + i) * info.nx + ei];
					const double H3_local[9] = {
						H3(it[0]), H3(it[1]), H3(it[2]),
						H3(it[3]), H3(it[4]), H3(it[5]),
						H3(it[6]), H3(it[7]), H3(it[8])}; // local mobility vector
					//const double dH3dH_local[9] = { H3dH(it[0]), dH3dH(it[1]), dH3dH(it[2]), dH3dH(it[3]), dH3dH(it[4]), dH3dH(it[5]), dH3dH(it[6]), dH3dH(it[7]), dH3dH(it[8]) };
					const double P_local[9] = {
						P(it[0]), P(it[1]), P(it[2]),
						P(it[3]), P(it[4]), P(it[5]),
						P(it[6]), P(it[7]), P(it[8])}; // local pressure vector

					for (int i = 0; i < 9; i++)
					{
						for (int j = 0; j < 9; j++)
						{
							double W_sum = 0;
							double dW_sum = 0;
							for (int k = 0; k < 9; k++)
							{ // assembly of local weighted stiff matrix equation (3.103)
								W_sum += H3_local[k] * (info.jy / info.jx * Mesh::weightedStiffDx[k][i][j] +
														info.jx / info.jy * Mesh::weightedStiffDy[k][i][j]);
								dW_sum += P_local[k] * ( // contraction between weighted stiff matrix and pressure vector  equation (3.106)
														   info.jy / info.jx * Mesh::weightedStiffDx[j][i][k] +
														   info.jx / info.jy * Mesh::weightedStiffDy[j][i][k]);
							}
							W.coeffRef(it[i], it[j]) += W_sum;
							dW.coeffRef(it[i], it[j]) += dW_sum * dH3dH(it[j]);
						}
					}
				}
			}
		}
	}
}

// This is how Mesh::outputMesh works:
// 0. Input = "path"
// 1. Ouput quad Q9 elements to a file = "path"/element.txt
// 2. Ouput quad Q9 nodal points to a file = "path"/node.txt
//    where node.txt : col 1 = X coordinate, col 2 = Y coordiante
void Mesh::outputMesh(const std::string &path) const
{

	std::string fileName = path + "/" + "element.txt";
	//const char *fileName = str.c_str();
	//FILE * file = fopen("./output/element.txt", "w");
	int err;
	FILE *file;
	err = fopen_s(&file, fileName.c_str(), "w+");
	if (err == 0)
	{
		for (auto &value : element)
		{
			for (auto &tmp : value)
			{
				fprintf(file, "%d\t", tmp);
			}
			fprintf(file, "\n"); // newline
		}
	}
	if (file)
	{
		err = fclose(file);
	}
	fileName = path + "/" + "node.txt";

	err = fopen_s(&file, fileName.c_str(), "w+");
	for (auto &value : node)
	{
		fprintf(file, "%16.16f\t", value[0]);
		fprintf(file, "%16.16f\n", value[1]);
	}
	if (file)
	{
		err = fclose(file);
	}
};

